// src/pages/StudyChallenge.js
import React, { useState, useEffect } from 'react';

const StudyChallenge = () => {
  const [seconds, setSeconds] = useState(1500); // 25 mins
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    let interval;
    if (isActive && seconds > 0) {
      interval = setInterval(() => {
        setSeconds((prev) => prev - 1);
      }, 1000);
    } else if (seconds === 0) {
      setIsActive(false);
      alert("ðŸŽ‰ Challenge Completed! Great job!");
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  const formatTime = (secs) => {
    const m = Math.floor(secs / 60);
    const s = secs % 60;
    return `${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-yellow-100 text-black">
      <h1 className="text-3xl font-bold mb-4">ðŸ“š Study Challenge</h1>
      <p className="text-xl mb-2">Stay focused for 25 minutes!</p>
      <div className="text-6xl font-mono mb-6">{formatTime(seconds)}</div>
      <button
        onClick={() => setIsActive(!isActive)}
        className="px-6 py-3 bg-yellow-500 text-white rounded-xl shadow-lg hover:bg-yellow-600 transition"
      >
        {isActive ? 'Pause' : 'Start Challenge'}
      </button>
    </div>
  );
};

export default StudyChallenge;
