const StudyChallenge = () => {
    const challenge = [
      "üìö Study for 25 minutes using the Pomodoro technique",
      "üìù Complete one topic from your weakest subject",
      "üîá Keep your phone away for 1 hour while studying",
      "üìñ Read 10 pages of your textbook or notes",
      "üìÜ Plan your schedule for tomorrow in your study journal",
    ];
  
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-400 to-indigo-600 text-white py-12 px-6">
        <h2 className="text-4xl font-bold text-center mb-10 drop-shadow-md">üí° Study Challenges</h2>
  
        <div className="max-w-3xl mx-auto grid grid-cols-1 sm:grid-cols-2 gap-6">
          {challenges.map((challenge, index) => (
            <div
              key={index}
              className="bg-white text-indigo-700 p-6 rounded-2xl shadow-lg font-medium hover:scale-105 transform transition duration-300"
            >
              {challenge}
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default StudyChallenge;
