// src/pages/DistractionDetection.js
import React, { useState } from 'react';

const DistractionDetection = () => {
  const [status, setStatus] = useState("Focused");

  const toggleStatus = () => {
    setStatus((prev) => (prev === "Focused" ? "Distracted" : "Focused"));
  };

  return (
    <div className={`flex flex-col items-center justify-center h-screen transition-all duration-500 ${
      status === "Focused" ? "bg-green-500" : "bg-red-500"
    } text-white`}>
      <h1 className="text-4xl font-bold mb-4">Distraction Detector</h1>
      <p className="text-xl mb-6">
        Current Status:{" "}
        <span className="font-extrabold underline">{status}</span>
      </p>
      <button
        className="bg-white text-black px-6 py-3 rounded-xl font-semibold hover:bg-gray-200 transition"
        onClick={toggleStatus}
      >
        Toggle Status (Test)
      </button>
    </div>
  );
};

export default DistractionDetection;
