// src/pages/DistractionDetector.js
import React, { useEffect, useRef, useState } from 'react';

const DistractionDetector = () => {
  const videoRef = useRef(null);
  const [status, setStatus] = useState('Checking for distractions...');

  useEffect(() => {
    const getCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
        videoRef.current.play();

        // Simulate detection (mock AI detection)
        setTimeout(() => {
          const distracted = Math.random() > 0.5;
          setStatus(distracted ? 'üòê You seem distracted!' : '‚úÖ Great focus!');
        }, 3000);
      } catch (err) {
        setStatus('‚ùå Unable to access camera.');
        console.error(err);
      }
    };

    getCamera();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-indigo-100 text-center">
      <h1 className="text-3xl font-bold mb-4">Distraction Detector</h1>
      <video ref={videoRef} className="w-64 h-48 rounded shadow-md mb-4" />
      <p className="text-xl">{status}</p>
    </div>
  );
};

export default DistractionDetector;
